// src/screens/HomeScreen.js
import React from 'react';
import { View, Text, StyleSheet, TouchableOpacity, TextInput } from 'react-native';
import Icon from 'react-native-vector-icons/MaterialCommunityIcons';

export default function HomeScreen({ navigation }) {
  return (
    <View style={styles.container}>
      {/* Search Bar */}
      <TextInput
        style={styles.searchBar}
        placeholder="Search LocalHive..."
        placeholderTextColor="#aaa"
      />

      {/* Icon Buttons */}
      <View style={styles.iconRow}>
        <TouchableOpacity style={styles.iconButton} onPress={() => navigation.navigate('News')}>
          <Icon name="newspaper" size={50} color="#1E90FF" />
          <Text style={styles.iconText}>News</Text>
        </TouchableOpacity>

        <TouchableOpacity style={styles.iconButton} onPress={() => navigation.navigate('Events')}>
          <Icon name="calendar" size={50} color="#32CD32" />
          <Text style={styles.iconText}>Events</Text>
        </TouchableOpacity>
      </View>

      {/* Plus Button in the Middle */}
      <TouchableOpacity style={styles.plusButton} onPress={() => navigation.navigate('Report')}>
        <Icon name="plus-circle" size={70} color="#FF6347" />
      </TouchableOpacity>

      <View style={styles.iconRow}>
        <TouchableOpacity style={styles.iconButton} onPress={() => navigation.navigate('Alerts')}>
          <Icon name="alert-circle" size={50} color="#FF6347" />
          <Text style={styles.iconText}>Alerts</Text>
        </TouchableOpacity>

        <TouchableOpacity style={styles.iconButton} onPress={() => navigation.navigate('Weather')}>
          <Icon name="weather-cloudy" size={50} color="#FFD700" />
          <Text style={styles.iconText}>Weather</Text>
        </TouchableOpacity>
      </View>

      {/* City-based Filter */}
      <View style={styles.cityFilter}>
        <Text style={styles.cityFilterText}>Filter by Zip Code</Text>
        <TextInput
          style={styles.cityInput}
          placeholder="Enter zip code"
          placeholderTextColor="#12321"
        />
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#F5F5F5',
    padding: 20,
    alignItems: 'center',
    justifyContent: 'flex-start',
  },
  searchBar: {
    width: '100%',
    height: 40,
    backgroundColor: '#fff',
    borderRadius: 10,
    paddingHorizontal: 10,
    marginBottom: 20,
    borderColor: '#ccc',
    borderWidth: 1,
  },
  iconRow: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    width: '100%',
    marginBottom: 20,
  },
  iconButton: {
    alignItems: 'center',
    justifyContent: 'center',
    width: '40%',
    padding: 10,
    backgroundColor: '#fff',
    borderRadius: 10,
    borderColor: '#ddd',
    borderWidth: 1,
  },
  iconText: {
    marginTop: 10,
    fontSize: 16,
    fontWeight: '500',
  },
  plusButton: {
    position: 'absolute',
    top: '40%',
    alignSelf: 'center',
    backgroundColor: '#fff',
    borderRadius: 35,
    elevation: 5,
  },
  cityFilter: {
    marginTop: 20,
    width: '100%',
    alignItems: 'center',
  },
  cityFilterText: {
    fontSize: 16,
    fontWeight: '600',
    marginBottom: 10,
  },
  cityInput: {
    width: '100%',
    height: 40,
    backgroundColor: '#fff',
    borderRadius: 10,
    paddingHorizontal: 10,
    borderColor: '#ccc',
    borderWidth: 1,
  },
});
